   unsigned   short   ds ,   __dsh ; 
   unsigned   long   edi ; 
   unsigned   long   esi ; 
   unsigned   long   ebp ; 
   unsigned   long   esp ; 
   unsigned   long   ebx ; 
   unsigned   long   edx ; 
   unsigned   long   ecx ; 
   unsigned   long   eax ; 
   unsigned   long   trapno ; 
   unsigned   long   err ; 
   unsigned   long   eip ; 
   unsigned   short   cs ,   __csh ; 
   unsigned   long   eflags ; 
   unsigned   long   esp_at_signal ; 
   unsigned   short   ss ,   __ssh ; 
   struct   _fpstate   *   fpstate ; 
   unsigned   long   oldmask ; 
   unsigned   long   cr2 ;  };    x64   struct   _fpstate  { 
   /* FPU environment matching the 64-bit FXSAVE layout.  */ 
   __uint16_t          cwd ; 
   __uint16_t          swd ; 
   __uint16_t          ftw ; 
   __uint16_t          fop ; 
   __uint64_t          rip ; 
   __uint64_t          rdp ; 
   __uint32_t          mxcsr ; 
   __uint32_t          mxcr_mask ; 
   struct   _fpxreg      _st [ 8 ]; 
   struct   _xmmreg      _xmm [ 16 ]; 
   __uint32_t          padding [ 24 ];  };  struct   sigcontext  { 
   __uint64_t   r8 ; 
