                 // 如果设置了perturb_type, 则将获取到的chunk初始化为 perturb_type ^ 0xff 
                 alloc_perturb ( p ,   bytes ); 
                 return   p ; 
             } 
         }

如果走到了这里，那说明对于用户所需的chunk，不能直接从其对应的合适的bin中获取chunk，所以我们需要来查找比当前 bin 更大的 fast bin ， small bin 或者 large bin。           /*             Search for a chunk by scanning bins, starting with next largest             bin. This search is strictly by best-fit; i.e., the smallest             (with ties going to approximately the least recently used) chunk             that fits is selected.             The bitmap avoids needing to check that most blocks are nonempty.             The particular case of skipping all bins during warm-up phases             when no chunks have been returned yet is faster than it might look.           */ 

         ++ idx ; 
         // 获取对应的bin 
         bin     =   bin_at ( av ,   idx ); 
         // 获取当前索引在binmap中的block索引 
         // #define idx2block(i) ((i) >> BINMAPSHIFT)  ,BINMAPSHIFT=5 
         // Binmap按block管理，每个block为一个int，共32个bit，可以表示32个bin中是否有空闲chunk存在 
         // 所以这里是右移5 
         block   =   idx2block ( idx ); 
         // 获取当前块大小对应的映射，这里可以得知相应的bin中是否有空闲块 
         map     =   av -> binmap [   block   ]; 
         // #define idx2bit(i) ((1U << ((i) & ((1U << BINMAPSHIFT) - 1)))) 
         // 将idx对应的比特位设置为1，其它位为0 
         bit     =   idx2bit ( idx ); 
         for   (;;)   {

/* Skip rest of block if there are no more set bits in this block.               */ 
             // 如果bit>map，则表示该 map 中没有比当前所需要chunk大的空闲块 
             // 如果bit为0，那么说明，上面idx2bit带入的参数为0。 
             if   ( bit   >   map   ||   bit   ==   0 )   { 
                 do   { 
                     // 寻找下一个block，直到其对应的map不为0。 
                     // 如果已经不存在的话，那就只能使用top chunk了 
                     if   ( ++ block   >=   BINMAPSIZE )   /* out of bins */ 
                         goto   use_top ; 
                 }   while   (( map   =   av -> binmap [   block   ])   ==   0 ); 
                 // 获取其对应的bin，因为该map中的chunk大小都比所需的chunk大，而且 
                 // map本身不为0，所以必然存在满足需求的chunk。 
                 bin   =   bin_at ( av ,   ( block   <<   BINMAPSHIFT )); 
                 bit   =   1 ; 
             }

/* Advance to bin with set bit. There must be one. */ 
             // 从当前map的最小的bin一直找，直到找到合适的bin。 
             // 这里是一定存在的 
             while   (( bit   &   map )   ==   0 )   { 
                 bin   =   next_bin ( bin ); 
                 bit   <<=   1 ; 
                 assert ( bit   !=   0 ); 
             }

/* Inspect the bin. It is likely to be non-empty */ 
             // 获取对应的bin 
             victim   =   last ( bin ); 

             /*  If a false alarm (empty bin), clear the bit. */ 
             // 如果victim=bin，那么我们就将map对应的位清0，然后获取下一个bin 
             // 这种情况发生的概率应该很小。 
             if   ( victim   ==   bin )   { 
                 av -> binmap [   block   ]   =   map   &=   ~ bit ;   /* Write through */ 
                 bin                   =   next_bin ( bin ); 
                 bit   <<=   1 ; 
             }

else   { 
                 // 获取对应victim的大小 
                 size   =   chunksize ( victim ); 

                 /*  We know the first chunk in this bin is big enough to use. */ 
                 assert (( unsigned   long )   ( size )   >=   ( unsigned   long )   ( nb )); 
                 // 计算分割后剩余的大小 
                 remainder_size   =   size   -   nb ; 

                 /* unlink */ 
                 unlink ( av ,   victim ,   bck ,   fwd ); 

                 /* Exhaust */ 
                 // 如果分割后不够一个chunk怎么办？ 
                 if   ( remainder_size   <   MINSIZE )   { 
                     set_inuse_bit_at_offset ( victim ,   size ); 
                     if   ( av   !=   & main_arena )   set_non_main_arena ( victim ); 
                 } 

                 /* Split */ 
                 // 如果够，尽管分割 
                 else   { 
                     // 计算剩余的chunk的偏移 
                     remainder   =   chunk_at_offset ( victim ,   nb ); 

                     /* We cannot assume the unsorted list is empty and therefore                         have to perform a complete insert here.  */ 
                     // 将剩余的chunk插入到unsorted bin中 
