     p   =   mem2chunk ( mem ); 
     // 如果该块内存是mmap得到的 
     if   ( chunk_is_mmapped ( p ))   /* release mmapped memory. */ 
     { 
         /* See if the dynamic brk/mmap threshold needs adjusting.         Dumped fake mmapped chunks do not affect the threshold.  */ 
         if   ( ! mp_ . no_dyn_threshold   &&   chunksize_nomask ( p )   >   mp_ . mmap_threshold   && 
             chunksize_nomask ( p )   <=   DEFAULT_MMAP_THRESHOLD_MAX   && 
             ! DUMPED_MAIN_ARENA_CHUNK ( p ))   { 
             mp_ . mmap_threshold   =   chunksize ( p ); 
             mp_ . trim_threshold   =   2   *   mp_ . mmap_threshold ; 
             LIBC_PROBE ( memory_mallopt_free_dyn_thresholds ,   2 , 
                        mp_ . mmap_threshold ,   mp_ . trim_threshold ); 
         } 
         munmap_chunk ( p ); 
         return ; 
     } 
     // 根据chunk获得分配区的指针 
     ar_ptr   =   arena_for_chunk ( p ); 
     // 执行释放 
     _int_free ( ar_ptr ,   p ,   0 );  }

函数初始时刻定义了一系列的变量，并且得到了用户想要释放的 chunk 的大小  static   void   _int_free ( mstate   av ,   mchunkptr   p ,   int   have_lock )   { 
     INTERNAL_SIZE_T   size ;        /* its size */ 
     mfastbinptr   *     fb ;          /* associated fastbin */ 
     mchunkptr         nextchunk ;   /* next contiguous chunk */ 
     INTERNAL_SIZE_T   nextsize ;    /* its size */ 
     int               nextinuse ;   /* true if nextchunk is used */ 
     INTERNAL_SIZE_T   prevsize ;    /* size of previous contiguous chunk */ 
     mchunkptr         bck ;         /* misc temp for linking */ 
     mchunkptr         fwd ;         /* misc temp for linking */ 

     const   char   * errstr   =   NULL ; 
     int           locked   =   0 ; 

     size   =   chunksize ( p );

/* Little security check which won't hurt performance: the         allocator never wrapps around at the end of the address space.         Therefore we can exclude some size values which might appear         here by accident or by "design" from some intruder.  */ 
     // 指针不能指向非法的地址, 必须小于等于-size，为什么？？？ 
     // 指针必须得对齐，2*SIZE_SZ 这个对齐得仔细想想 
     if   ( __builtin_expect (( uintptr_t )   p   >   ( uintptr_t )   - size ,   0 )   || 
         __builtin_expect ( misaligned_chunk ( p ),   0 ))   { 
         errstr   =   "free(): invalid pointer" ; 
     errout : 
         if   ( ! have_lock   &&   locked )   __libc_lock_unlock ( av -> mutex ); 
         malloc_printerr ( check_action ,   errstr ,   chunk2mem ( p ),   av ); 
         return ; 
     } 
     /* We know that each chunk is at least MINSIZE bytes in size or a         multiple of MALLOC_ALIGNMENT.  */ 
     // 大小没有最小的chunk大，或者说，大小不是MALLOC_ALIGNMENT的整数倍 
     if   ( __glibc_unlikely ( size   <   MINSIZE   ||   ! aligned_OK ( size )))   { 
         errstr   =   "free(): invalid size" ; 
         goto   errout ; 
     } 
     // 检查该chunk是否处于使用状态，非调试状态下没有作用 
     check_inuse_chunk ( av ,   p );   其中  /* Check if m has acceptable alignment */  #define aligned_OK(m) (((unsigned long) (m) &MALLOC_ALIGN_MASK) == 0)  #define misaligned_chunk(p)                                                    \      ((uintptr_t)(MALLOC_ALIGNMENT == 2 * SIZE_SZ ? (p) : chunk2mem(p)) &       \       MALLOC_ALIGN_MASK)

如果上述检查都合格的话，判断当前的 bin 是不是在 fast bin 范围内，在的话就插入到  fastbin 头部 ，即成为对应 fastbin 链表的 第一个 free chunk 。       /*        If eligible, place chunk on a fastbin so it can be found        and used quickly in malloc.      */ 

     if   (( unsigned   long )   ( size )   <=   ( unsigned   long )   ( get_max_fast ())  #if TRIM_FASTBINS 
         /*        If TRIM_FASTBINS set, don't place chunks        bordering top into fastbins          */ 
        //默认 #define TRIM_FASTBINS 0，因此默认情况下下面的语句不会执行 
        // 如果当前chunk是fast chunk，并且下一个chunk是top chunk，则不能插入 
         &&   ( chunk_at_offset ( p ,   size )   !=   av -> top )  #endif 
             )   { 
         // 下一个chunk的大小不能小于两倍的SIZE_SZ,并且 
         // 下一个chunk的大小不能大于system_mem， 一般为132k 
         // 如果出现这样的情况，就报错。 
         if   ( __builtin_expect ( 
                 chunksize_nomask ( chunk_at_offset ( p ,   size ))   <=   2   *   SIZE_SZ ,   0 )   || 
             __builtin_expect ( 
                 chunksize ( chunk_at_offset ( p ,   size ))   >=   av -> system_mem ,   0 ))   { 
             /* We might not have a lock at this point and concurrent                 modifications                 of system_mem might have let to a false positive.  Redo the test                 after getting the lock.  */ 
             if   ( have_lock   ||   ({ 
                     assert ( locked   ==   0 ); 
                     __libc_lock_lock ( av -> mutex ); 
                     locked   =   1 ; 
                     chunksize_nomask ( chunk_at_offset ( p ,   size ))   <=   2   *   SIZE_SZ   || 
                         chunksize ( chunk_at_offset ( p ,   size ))   >=   av -> system_mem ; 
                 }))   { 
                 errstr   =   "free(): invalid next size (fast)" ; 
                 goto   errout ; 
             } 
             if   ( ! have_lock )   { 
                 __libc_lock_unlock ( av -> mutex ); 
                 locked   =   0 ; 
             } 
         } 
         // 将chunk的mem部分全部设置为perturb_byte  
         free_perturb ( chunk2mem ( p ),   size   -   2   *   SIZE_SZ ); 
         // 设置fast chunk的标记位 
         set_fastchunks ( av ); 
         // 根据大小获取fast bin的索引 
         unsigned   int   idx   =   fastbin_index ( size ); 
         // 获取对应fastbin的头指针，被初始化后为NULL。 
         fb                 =   & fastbin ( av ,   idx ); 

         /* Atomically link P to its fastbin: P->FD = *FB; *FB = P;  */ 
         // 使用原子操作将P插入到链表中 
         mchunkptr      old       =   * fb ,   old2 ; 
         unsigned   int   old_idx   =   ~ 0u ; 
         do   { 
             /* Check that the top of the bin is not the record we are going to                 add                 (i.e., double free).  */ 
             // so we can not double free one fastbin chunk 
             // 防止对 fast bin double free 
             if   ( __builtin_expect ( old   ==   p ,   0 ))   { 
                 errstr   =   "double free or corruption (fasttop)" ; 
                 goto   errout ; 
             } 
             /* Check that size of fastbin chunk at the top is the same as                 size of the chunk that we are adding.  We can dereference OLD                 only if we have the lock, otherwise it might have already been                 deallocated.  See use of OLD_IDX below for the actual check.  */ 
             if   ( have_lock   &&   old   !=   NULL ) 
                 old_idx   =   fastbin_index ( chunksize ( old )); 
             p -> fd   =   old2   =   old ; 
