         if   ( have_lock   &&   old   !=   NULL   &&   __builtin_expect ( old_idx   !=   idx ,   0 ))   { 
             errstr   =   "invalid fastbin entry (free)" ; 
             goto   errout ; 
         } 
     }

只有不是 fast bin 的情况下才会触发unlink  首先我们先说一下为什么会合并chunk，这是为了避免heap中有太多零零碎碎的内存块，合并之后可以用来应对更大的内存块请求。合并的主要顺序为   先考虑物理低地址空闲块  后考虑物理高地址空闲块   合并后的 chunk 指向合并的 chunk 的低地址。  在没有锁的情况下，先获得锁。       /*        Consolidate other non-mmapped chunks as they arrive.      */ 

     else   if   ( ! chunk_is_mmapped ( p ))   { 
         if   ( ! have_lock )   { 
             __libc_lock_lock ( av -> mutex ); 
             locked   =   1 ; 
         } 
         nextchunk   =   chunk_at_offset ( p ,   size );

/* Lightweight tests: check whether the block is already the             top block.  */ 
         // 当前free的chunk不能是top chunk 
         if   ( __glibc_unlikely ( p   ==   av -> top ))   { 
             errstr   =   "double free or corruption (top)" ; 
             goto   errout ; 
         } 
         // 当前free的chunk的下一个chunk不能超过arena的边界 
         /* Or whether the next chunk is beyond the boundaries of the arena.  */ 
         if   ( __builtin_expect ( contiguous ( av )   && 
                                  ( char   * )   nextchunk   >= 
                                      (( char   * )   av -> top   +   chunksize ( av -> top )), 
                              0 ))   { 
             errstr   =   "double free or corruption (out)" ; 
             goto   errout ; 
         } 
         // 当前要free的chunk的使用标记没有被标记，double free 
         /* Or whether the block is actually not marked used.  */ 
         if   ( __glibc_unlikely ( ! prev_inuse ( nextchunk )))   { 
             errstr   =   "double free or corruption (!prev)" ; 
             goto   errout ; 
         } 
         // 下一个chunk的大小 
         nextsize   =   chunksize ( nextchunk ); 
         // next chunk size valid check 
         // 判断下一个chunk的大小是否不大于2*SIZE_SZ，或者 
         // nextsize是否大于系统可提供的内存 
         if   ( __builtin_expect ( chunksize_nomask ( nextchunk )   <=   2   *   SIZE_SZ ,   0 )   || 
             __builtin_expect ( nextsize   >=   av -> system_mem ,   0 ))   { 
             errstr   =   "free(): invalid next size (normal)" ; 
             goto   errout ; 
         }

/* consolidate backward */ 
         if   ( ! prev_inuse ( p ))   { 
             prevsize   =   prev_size ( p ); 
             size   +=   prevsize ; 
             p   =   chunk_at_offset ( p ,   - (( long )   prevsize )); 
             unlink ( av ,   p ,   bck ,   fwd ); 
         }

需要注意的是，如果下一块不是 top chunk 后，则合并高地址的 chunk ，并将合并后的 chunk 放入到unsorted bin中。           // 如果下一个chunk不是top chunk 
         if   ( nextchunk   !=   av -> top )   { 
             /* get and clear inuse bit */ 
             // 获取下一个 chunk 的使用状态 
             nextinuse   =   inuse_bit_at_offset ( nextchunk ,   nextsize ); 
             // 如果不在使用，合并，否则清空当前chunk的使用状态。 
             /* consolidate forward */ 
             if   ( ! nextinuse )   { 
                 unlink ( av ,   nextchunk ,   bck ,   fwd ); 
                 size   +=   nextsize ; 
             }   else 
                 clear_inuse_bit_at_offset ( nextchunk ,   0 ); 

