             if   ( __glibc_unlikely ( fwd -> bk   !=   bck ))   { 
                 errstr   =   "free(): corrupted unsorted chunks" ; 
                 goto   errout ; 
             } 
             p -> fd   =   fwd ; 
             p -> bk   =   bck ; 
