             if   ( ! in_smallbin_range ( size ))   { 
                 p -> fd_nextsize   =   NULL ; 
                 p -> bk_nextsize   =   NULL ; 
             } 
             bck -> fd   =   p ; 
             fwd -> bk   =   p ; 

             set_head ( p ,   size   |   PREV_INUSE ); 
             set_foot ( p ,   size ); 

             check_free_chunk ( av ,   p ); 
         }

/*            If the chunk borders the current high end of memory,            consolidate into top          */ 
         // 如果要释放的chunk的下一个chunk是top chunk，那就合并到 top chunk 
         else   { 
             size   +=   nextsize ; 
             set_head ( p ,   size   |   PREV_INUSE ); 
             av -> top   =   p ; 
             check_chunk ( av ,   p ); 
