   for   (   i   =   0 ;   i   <=   15 ;   ++ i   ) 
   { 
     if   (   !* ( _DWORD   * )( 24LL   *   i   +   a1 )   ) 
     { 
       printf ( "Size: " ); 
       v2   =   read_num (); 
       if   (   v2   >   0   ) 
       { 
         if   (   v2   >   4096   ) 
           v2   =   4096 ; 
         v3   =   calloc ( v2 ,   1uLL ); 
         if   (   ! v3   ) 
           exit ( - 1 ); 
